mapper.select:
SELECT *
FROM SYS_PERMISSION a
WHERE id = #{id}

mapper.update:
UPDATE SYS_PERMISSION a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.select:

		SELECT * 
		FROM SYS_PERMISSION a
		WHERE 1 = 1

			AND a.name = #{name} 


			AND a.isEnable = #{isEnable} 

     	ORDER  BY a.pId asc, a.sort asc


		SELECT * 
		FROM SYS_PERMISSION a
		WHERE 1 = 1

			AND a.name = #{name} 


			AND a.isEnable = #{isEnable} 

     	ORDER BY a.sort DESC

mapper.delete:
DELETE FROM SYS_PERMISSION


		WHERE 1 != 1


		WHERE `id` = #{id}


		WHERE `id` in #{id}
