mapper.select:
SELECT * 
FROM SYS_ROLE a
WHERE id = #{id}

mapper.update:
UPDATE SYS_ROLE a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.delete:
DELETE FROM SYS_ROLE


		WHERE 1 != 1


		WHERE `id` = #{id}


		WHERE `id` in #{id}

mapper.select:
select r.id, r.code from sys_role  r inner join sys_re_user_role  u 
on r.id = u.roleId 
and u.userId = #{userId};
